UPDATE ar_quran_translations SET footnotes_haleem = NULL WHERE translation_haleem IS NOT NULL;
UPDATE ar_quran_translations SET footnotes_haleem = '[{"marker":"a","text":"Most occurrences of this term rahman in the Quran are in the context of Him being mighty and majestic as well as merciful. The addition of the word ‘Lord’ here is intended to convey this aspect of the term."},{"marker":"b","text":"This term rahim is an intensive form suggesting that the quality of giving mercy is inherent in God’s nature."},{"marker":"c","text":"This is the only instance where this formula, present at the start of every sura but one, is counted as the first numbered verse."}]' WHERE surah = 1 AND ayah = 1;
UPDATE ar_quran_translations SET footnotes_haleem = '[{"marker":"d","text":"The Arabic root r–b–b has connotations of caring and nurturing in addition to lordship, and this should be borne in mind wherever the term occurs and is rendered ‘lord’."},{"marker":"e","text":"Al- '' alamin in Arabic means all the worlds, of mankind, angels, animals, plants, this world, the next, and so forth."}]' WHERE surah = 1 AND ayah = 2;
UPDATE ar_quran_translations SET footnotes_haleem = '[{"marker":"f","text":"Note that the verb here is not attributed to God."}]' WHERE surah = 1 AND ayah = 7;
UPDATE ar_quran_translations SET footnotes_haleem = '[{"marker":"a","text":"These are the names of the three Arabic letters a , l , and m . Twenty-nine suras of the Quran begin with separate alphabetical letters like these, from one individual letter up to five. Various interpretations have been offered. It is sufficient to mention two here: ( 1 ) these letters indicated to the Arabs who first heard the Quran that the Quran consists of letters and words of their own language, although it was superior to any speech of their own, being of divine origin; ( 2 ) they are an exclamatory device intended to arrest the listeners’ attention, similar to the custom of starting poems with an emphatic ‘No!’ or ‘Indeed!’ Exegetes normally added, after expounding their theories, ‘God knows best.’"}]' WHERE surah = 2 AND ayah = 1;
UPDATE ar_quran_translations SET footnotes_haleem = '[{"marker":"b","text":"The Arabic construction la rayba fihi carries more than one meaning, including ‘there is nothing dubious about/in it’ and ‘it is not to be doubted’ as regards its origin or contents."}]' WHERE surah = 2 AND ayah = 2;
UPDATE ar_quran_translations SET footnotes_haleem = '[{"marker":"c","text":"The root w–q–y in this morphological form has the meaning of being mindful or being wary of something. The opposite of being mindful of God is to ignore Him or have no reference to Him in your thought, feeling, or action. This is a fundamental concept about God and the believers’ relation to Him. Many translators render the term as ‘those who fear God’, but this is an over-expression of the term and does not correctly convey the meaning of the concept, which is a very common one in the Quran."},{"marker":"d","text":"What is beyond their perception, literally ‘absent’– –this applies to the nature of God, the Hereafter, historical information not witnessed, etc."}]' WHERE surah = 2 AND ayah = 3;
UPDATE ar_quran_translations SET footnotes_haleem = '[{"marker":"a","text":"This means regular and proper performance of the formal prayer ( salah ), as taught by the Prophet Muhammad."},{"marker":"b","text":"Yunfiquna in the Arabic of the Quran literally means ‘spend’, on others, in good causes, in the way of God."}]' WHERE surah = 2 AND ayah = 4;
UPDATE ar_quran_translations SET footnotes_haleem = '[{"marker":"c","text":"The basic meaning of''adhab is ‘to restrain (from doing wrong)’, extended to mean anything difficult or painful, punishment, famine (see 23 : 78 ). See Majaz al-Quran , by''Izz al-Din Ibn''Abdul-Salam (London: Al-Furqan Foundation, 1999 ), 194 , and E. W. Lane, Arabic–English Lexicon (Beirut: Librairie du Liban, 1968 )."}]' WHERE surah = 2 AND ayah = 8;
