UPDATE ar_quran_translations SET footnotes_haleem = '[{"marker":"a","text":"The term khalifa is normally translated as ‘vicegerent’ or ‘deputy’. While this is one meaning of the term, its basic meaning is ‘successor’– – the Quran often talks about generations and individuals who are successors to each other, cf. 6 : 165 , 7 : 129 , etc.– – or a ‘trustee’ to whom a responsibility is temporarily given, cf. Moses and Aaron, 7 : 142 ."},{"marker":"b","text":"This is istifham inkari in Arabic, i.e. not an interrogative but an exclamatory statement normally expressing disapproval, best rendered in English by ‘How can you’. There are numerous examples of this in the Quran."}]' WHERE surah = 2 AND ayah = 31;
