UPDATE ar_quran_translations SET footnotes_haleem = '[{"marker":"a","text":"The Sabians were a monotheistic religious community. See M. Asad, The Message of the Quran (Gibraltar: Dar al-Andalus, 1997 ), 40 n. 49 ."},{"marker":"b","text":"This is understood by some as ‘physically turn into apes’ but in fact it is a figure of speech– – the structure ‘be apes’ is like ‘be stones/iron’ in 17 : 50 . Just as the Quran describes the disbelievers as blind, deaf, and dumb, here the transgressors are apes."}]' WHERE surah = 2 AND ayah = 67;
